const math = require('mathjs');

let currentValue = '';
let expression = [];
let result = '';


class CalcFunctions {

    constructor() {
        currentValue = '';
        expression = [];
        result = '';
    }

    getCurrentValue() {
        return currentValue;
    }

    getExpression() {
        return expression;
    }

    getResult() {
        return result;
    }

    enterDigit(digit) {
        currentValue += digit;
    }

    clearEvery() {
        if (currentValue === '') {
            return;
        }
        expression = [];
        currentValue = '';


    }

    percentage() {
        if (currentValue === '') {
            return;
        }

        currentValue = math.evaluate(currentValue + " / 100");
    }

    decimal() {
        if (currentValue === '') {
            currentValue = "0."
            return;
        }

        let lastChar = currentValue.charAt(currentValue.length - 1);
        console.log("lastchar", lastChar);
        if (lastChar !== '.') {
            currentValue = currentValue + '.';
        }
    }

    plusMinus() {
        if (currentValue === '') {
            return;
        }

        if (currentValue.charAt(0) === '-') {
            console.log("negative!!");
            currentValue = currentValue.slice(1);
        }
        else {
            currentValue = "-" + currentValue;
        }
    }

    add() {
        if (currentValue === '') {
            return;
        }

        document.getElementById("add").classList.add('active');
        console.log("value?", this);
        expression.push(currentValue);
        expression.push('+');
        currentValue = '';
    }

    subtract() {
        if (currentValue === '') {
            return;
        }

        expression.push(currentValue);
        expression.push('-');

        currentValue = '';
    }

    multiply() {
        if (currentValue === '') {
            return;
        }

        expression.push(currentValue);
        expression.push('*');
        currentValue = '';
    }

    divide() {
        if (currentValue === '') {
            return;
        }

        expression.push(currentValue);
        expression.push('/');
        currentValue = '';

    }

    equals() {
        if (currentValue === '') {
            return;
        }

        expression.push(currentValue);

        const joinedExpression = expression.join(' ');
        console.log("expression", joinedExpression);

        result = math.evaluate(joinedExpression);
        currentValue = result;
        expression = [];

    }
}

export default CalcFunctions;
