const math = require('mathjs');

let currentValue = '';
let expression = [];
let result = '';


class CalcFunctions {

    constructor() {
        currentValue = '';
        expression = [];
        result = '';
    }

    getCurrentValue() {
        return currentValue;
    }

    getExpression() {
        return expression;
    }

    getResult() {
        return result;
    }

    enterDigit(digit) {
        currentValue += digit;
    }

    clearEvery() {
        if (currentValue === '') {
            return;
        }
        expression = [];
        currentValue = '';


    }


    add() {
        if (currentValue === '') {
            return;
        }

        expression.push(currentValue);
        expression.push('+');
        currentValue = '';
        console.log("expression", this.expression)
    }

    subtract() {
        if (currentValue === '') {
            return;
        }

        expression.push(currentValue);
        expression.push('-');

        currentValue = '';
    }

    multiply() {
        if (currentValue === '') {
            return;
        }

        expression.push(currentValue);
        expression.push('*');
        currentValue = '';
    }

    divide() {
        if (currentValue === '') {
            return;
        }

        expression.push(currentValue);
        expression.push('/');
        currentValue = '';

    }

    equals() {
        if (currentValue === '') {
            return;
        }

        expression.push(currentValue);

        const joinedExpression = expression.join(' ');
        console.log("expression", joinedExpression);

        result = math.evaluate(joinedExpression);
        currentValue = result;
        expression = [];

    }
}

export default CalcFunctions;
