const math = require("mathjs");

let currentValue = "";
let expression = [];
let result = "";
let lastOperation = [];

class CalcFunctions {
  constructor() {
    currentValue = "0";
    expression = [];
    result = "";
    lastOperation = [];

  }

  getCurrentValue() {
    return currentValue;
  }

  getExpression() {
    return expression;
  }

  getResult() {
    return result;
  }

  enterDigit(digit) {
    currentValue += digit;
  }

  clearEvery() {
    if (currentValue === "") {
      return;
    }
    this.deactiveButton();
    currentValue = "0";
    expression = [];
    lastOperation = [];
  }

  percentage() {
    if (currentValue === "") {
      return;
    }

    currentValue = math.evaluate(currentValue + " / 100");
  }

  decimal() {
    if (currentValue === "") {
      currentValue = "0.";
      return;
    }

    let lastChar = currentValue.charAt(currentValue.length - 1);
    if (lastChar !== ".") {
      currentValue = currentValue + ".";
    }
  }

  plusMinus() {
    if (currentValue === "") {
      return;
    }

    if (currentValue.charAt(0) === "-") {
      currentValue = currentValue.slice(1);
    } else {
      currentValue = "-" + currentValue;
    }
  }

  add() {
    if (currentValue === "") {
      return;
    }

    expression.push(currentValue);
    expression.push("+");
    currentValue = "";
  }

  subtract() {
    if (currentValue === "") {
      return;
    }

    expression.push(currentValue);
    expression.push("-");

    currentValue = "";
  }

  multiply() {
    if (currentValue === "") {
      return;
    }

    expression.push(currentValue);
    expression.push("*");
    currentValue = "";
  }

  divide() {
    if (currentValue === "") {
      return;
    }

    expression.push(currentValue);
    expression.push("/");
    currentValue = "";
  }

  deactiveButton() {
    let active = document.getElementById("keypad").querySelectorAll(".active");
    console.log("active: ", active.length);

    if (active.length > 0) {
      active[0].classList.remove("active");
    }
  }

  equals() {
    if (currentValue === "") {
      return;
    }

    this.deactiveButton();
    console.log("expression1", expression);


    // if expression is empty
    if (!expression.length > 0) {
      console.log("CV", currentValue);
      lastOperation.unshift(currentValue);
      expression = lastOperation;
      lastOperation = [];
      console.log("expression2", expression);

    }
    else {
      expression.push(currentValue);
      lastOperation = [];
    }
    console.log("expression3", expression);

    const joinedExpression = expression.join(" ");

    result = math.evaluate(joinedExpression).toString();
    currentValue = result;

    let expressionLength = expression.length;
    lastOperation.push(expression[expressionLength - 2]);
    lastOperation.push(expression[expressionLength - 1]);
    console.log("lasOperation", lastOperation);
    expression = [];
  }
}

export default CalcFunctions;
