const math = require('mathjs');

let currentValue = '';
let expression = [];
let result = '';


class CalcFunctions {

    constructor() {
        currentValue = '';
        expression = [];
        result = '';
    }

    getCurrentValue() {
        return currentValue;
    }

    getExpression() {
        return expression;
    }

    getResult() {
        return result;
    }

    enterDigit(digit) {
        currentValue += digit;
    }

    add() {
        if (currentValue === '') {
            return;
        }

        expression.push(currentValue);
        expression.push('+');
    }

    subtract() {
        if (currentValue === '') {
            return;
        }

        expression.push(currentValue);
        expression.push('-');
    }

    multiply() {
        if (currentValue === '') {
            return;
        }

        expression.push(currentValue);
        expression.push('*');
    }

    divide() {
        if (currentValue === '') {
            return;
        }

        expression.push(currentValue);
        expression.push('/');
    }
}
