import React from "react";
import Button from "react-bootstrap/Button";
import PropTypes from "prop-types";

class Keypad extends React.Component {
  handleOnNumber = e => {
    this.props.onNumber(e.target.value);
  };

  render() {
    return (
      <div className={"keypad container w-25"} id="keypad">
        <div className="key-row row">
          <Button
            className="col"
            value="clear-every"
            onClick={this.props.onClearEvery}
          >
            CE
          </Button>
          <Button
            className="col"
            value="plusMinus"
            onClick={this.props.onPlusMinus}
          >
            +/-
          </Button>
          <Button
            className="col"
            value="percentage"
            onClick={this.props.onPercentage}
          >
            %
          </Button>
          <Button
            className="col"
            id="divide"
            value="divide"
            onClick={this.props.onDivide}
          >
            /
          </Button>
        </div>
        <div className="key-row row">
          <Button className="col" value="7" onClick={this.handleOnNumber}>
            7
          </Button>
          <Button className="col" value="8" onClick={this.handleOnNumber}>
            8
          </Button>
          <Button className="col" value="9" onClick={this.handleOnNumber}>
            9
          </Button>
          <Button
            className="col"
            id="multiply"
            value="multiply"
            onClick={this.props.onMultiply}
          >
            *
          </Button>
        </div>
        <div className="key-row row">
          <Button className="col" value="4" onClick={this.handleOnNumber}>
            4
          </Button>
          <Button className="col" value="5" onClick={this.handleOnNumber}>
            5
          </Button>
          <Button className="col" value="6" onClick={this.handleOnNumber}>
            6
          </Button>
          <Button
            className="col"
            id="subtract"
            value="subtract"
            onClick={this.props.onSubtract}
          >
            -
          </Button>
        </div>
        <div className="key-row row">
          <Button className="col" value="1" onClick={this.handleOnNumber}>
            1
          </Button>
          <Button className="col" value="2" onClick={this.handleOnNumber}>
            2
          </Button>
          <Button className="col" value="3" onClick={this.handleOnNumber}>
            3
          </Button>
          <Button
            className="col"
            id="add"
            value="add"
            onClick={this.props.onAdd}
          >
            +
          </Button>
        </div>
        <div className="key-row row ">
          <Button className={"col-6"} value="0" onClick={this.handleOnNumber}>
            0
          </Button>
          <Button className="col-3" value="." onClick={this.props.onDecimal}>
            .
          </Button>
          <Button className="col-3" value="=" onClick={this.props.onEquals}>
            =
          </Button>
        </div>
      </div>
    );
  }
}

