import React from "react";
import Button from 'react-bootstrap/Button';
import PropTypes from "prop-types";


const Keypad = (props) => {

    const handleOnNumber = (e) => {
        console.log("targetValue", e.targetValue);
        props.onNumber(e.target.value);
    };

    return (
        <div className="keypad">
            <div className='key-row '>
                <Button class='col' value='clear-every' onClick={props.onClearEvery}>CE</Button>
                <Button value='plusMinus' onClick={props.onPlusMinus}>+/-</Button>
                <Button value='percentage' onClick={props.onPercentage}>%</Button>
                <Button value='divide' onClick={props.onDivide}>/</Button>
            </div>
            <div className='key-row '>
                <Button className='' value='7' onClick={handleOnNumber}>7</Button>
                <Button value='8' onClick={handleOnNumber}>8</Button>
                <Button value='9' onClick={handleOnNumber}>9</Button>
                <Button value='multiply' onClick={props.onMultiply}>*</Button>
            </div>
            <div className='key-row '>
                <Button value='4' onClick={handleOnNumber}>4</Button>
                <Button value='5' onClick={handleOnNumber}>5</Button>
                <Button value='6' onClick={handleOnNumber}>6</Button>
                <Button value='subtract' onClick={props.onSubtract}>-</Button>
            </div>
            <div className='key-row '>
                <Button value='1' onClick={handleOnNumber}>1</Button>
                <Button value='2' onClick={handleOnNumber}>2</Button>
                <Button value='3' onClick={handleOnNumber}>3</Button>
                <Button value='add' onClick={props.onAdd}>+</Button>
            </div>
            <div className='key-row '>
                <Button value='0' onClick={handleOnNumber}>0</Button>
                <Button value='.' onClick={props.onDecimal}>.</Button>
                <Button value='=' onClick={props.onEquals}>=</Button>
            </div>
        </div>

    )
}

Keypad.defaultProps = {
    onNumber: digit => alert(digit)
}
Keypad.propTypes = {
    onNumber: PropTypes.func,
    onClearEvery: PropTypes.func,
    onAdd: PropTypes.func,
    onEquals: PropTypes.func,
    onDecimal: PropTypes.func,
    onSubtract: PropTypes.func,
    onDivide: PropTypes.func,
    onMultiply: PropTypes.func,
    onPlusMinus: PropTypes.func
};

export default Keypad;
